import * as Vue from "vue/dist/vue";

// Define a new component called button-counter
export default Vue.component("vueElement", {
  data: function() {
    return {
      count: 0
    };
  },
  props: ["width", "height"],

  template:
    "<div v-bind:style=\"{ width: width + 'px', height: height + 'px'}\"><button v-on:click=\"count++\">You clicked me {{ count }} times.</button></div>"
});
