import * as React from "react";
import { PropertyControls, ControlType } from "framer";
import { Checkbox } from "@bulb/patterns/modules/Checkbox";

interface Props {
  text: string;
  width: number;
  height: number;
  checked: boolean;
}

export class checkbox extends React.Component {
  static defaultProps = {
    text: "check me",
    checked: false
  };

  static propertyControls: PropertyControls<Props> = {
    text: { type: ControlType.String, title: "Label" },
    checked: { type: ControlType.Boolean, title: "Checked" }
  };

  render() {
    const { text, checked, width, height } = this.props;
    return (
      <Checkbox
        checked={checked}
        label={text}
        style={{
          width
        }}
      />
    );
  }
}
