import * as React from "react";
import { PropertyControls, ControlType } from "framer";
import { Pathway} from "@bulb/patterns/modules/ButtonsAndLinks/Pathway";

interface Props {
  text: string;
  width: number;
  height: number;
  fullWidth: boolean;
  purpose: string;
}

export class pathway extends React.Component {
  static defaultProps = {
    text: "Click me",
    fullWidth: false,
    purpose: "primary"
  };

  static propertyControls: PropertyControls<Props> = {
    text: { type: ControlType.String, title: "Title" },
    fullWidth: { type: ControlType.Boolean, title: "Full Width" },
    purpose: {
      type: ControlType.SegmentedEnum,
      title: "Style",
      options: ["primary", "inverted-primary", "plain"],
      optionTitles: ["primary", "inverted", "plain"]
    }
  };

  render() {
    const { text, fullWidth, width, height, purpose } = this.props;

    return (
      <div
        style={{
          width,
          height,
          display: "flex",
          alignItems: "center",
          justifyContent: "center"
        }}
      >
        <Pathway
          purpose={purpose || pathway.defaultProps.text}
          fullWidth={fullWidth}
        >
          {text || pathway.defaultProps.text}
        </Pathway>
      </div>
    );
  }
}
